import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

import { BlockEditable } from 'markdown-translatable';

const style = { padding: '0px' };
const inputFilters = [[/<br>/gi, '\n']];
const outputFilters = [[/\n/gi, '<br>']];
const blockStyle = {display: 'inline-block', width: '50%'};

const Cell = ({
  // classes,
  value,
  tableMeta,
  tableMeta: {
    columnIndex,
    rowIndex,
    rowData,
  },
  rowHeader,
  preview,
}) => {
  const [original, translation] = value.split('\t');
  let component;

  const onEdit = (markdown) => {
    debugger
    // editCell({rowIndex, columnIndex, value: markdown});
  };

  if (value === 'rowHeader' && rowHeader) {
    component = rowHeader(rowData);
  } else {
    const originalComponent = (
      <BlockEditable
        style={style}
        raw={!preview}
        markdown={original}
        editable={false}
        inputFilters={inputFilters}
        outputFilters={outputFilters}
      />
    );
    const translationComponent = (
      <BlockEditable
        style={style}
        raw={!preview}
        markdown={translation}
        editable={true}
        inputFilters={inputFilters}
        outputFilters={outputFilters}
        onEdit={onEdit}
      />
    );
    component = (
      <>
        <div className={classes.original} style={blockStyle}>
          {originalComponent}
        </div>
        <div style={blockStyle}>
          {translationComponent}
        </div>
      </>
    );
  }

  return component;
};

Cell.propTypes = {
  // classes: PropTypes.object.isRequired,
};

const styles = theme => ({
  root: {
  },
  original: {
    background: '#eee4',
  },
  translation: {
  },
});

const StyleComponent = withStyles(styles)(Cell);
export default StyleComponent;
