import { createMuiTheme } from '@material-ui/core/styles';

export const getMuiTheme = createMuiTheme({
  typography: {
    useNextVariants: true,
  },
  overrides: {
    MuiTableCell: {
      root: {
        padding: '0',
        textAlign: 'unset',
        display: 'block',
        borderBottom: 'none',
        '&:last-child': {
          paddingRight: 0,
        },
        '&:first-child': {
          position: 'sticky',
          top: 100,
        }
      },
      body: {
        fontSize: '1em',
      },
    },
    MuiTableRow: {
      root: {
        height: 'unset',
        padding: '0',
        display: 'block',
        borderBottom: '1px solid rgba(224, 224, 224, 1)',
      }
    },
    MUIDataTable: {
      root: {
      },
      responsiveScroll: {
        maxHeight: 'unset',
        overflowX: 'unset',
        overflowY: 'unset',
      },
    },
    MuiToolbar: {
      root: {
        top: 0,
        position: 'sticky',
        background: 'white',
        zIndex: '100',
      },
    },
    MUIDataTableHeadRow: {
      root: {
        display: 'none',
      },
    },
    MuiTable: {
      root: {
        position: 'sticky',
        bottom: 0,
        background: 'white',
      },
    },
  }
});
