import React, {useState} from 'react';
import PropTypes from 'prop-types';
import MUIDataTable from "mui-datatables";
import { MuiThemeProvider } from '@material-ui/core/styles';

import { getMuiTheme } from './muiTheme';
import Cell from './Cell';
import CustomToolbar from './CustomToolbar';

import { parseDataTables } from './helpers';
import { Typography } from '@material-ui/core';

function DataTable ({
  original,
  translation,
  options,
  ...props
}) {
  const [rowsPerPage, setRowsPerPage] = useState(25);
  const [preview, setPreview] = useState(true);

  const togglePreview = () => setPreview(!preview);
  const onSave = () => {};

  const _options = {
    responsive: 'scroll',
    fixedHeader: false,
    resizableColumns: false,
    selectableRows: 'none',
    rowHover: false,
    rowsPerPage: rowsPerPage,
    rowsPerPageOptions: [25, 50, 100],
    onChangeRowsPerPage: setRowsPerPage,
    download: false,
    print: false,
    customToolbar: () => (
      <CustomToolbar
        preview={preview}
        onPreview={togglePreview}
        onSave={onSave}
      />
    ),
    ...options
  };

  const rowHeader = (rowData) => {
    const book = rowData[0+1].split('\t')[0];
    const chapter = rowData[1+1].split('\t')[0];
    const verse = rowData[2+1].split('\t')[0];
    const id = rowData[3+1].split('\t')[0];
    const component = (
      <Typography variant='h6' style={{padding: '0 0.5em'}}>
        {`${book} ${chapter}:${verse}`}
      </Typography>
    );
    return component;
  };
  const hideColumns = [0,1,2,3,6];
  const noFilter = [0,3,5,6,7,8];
  const compositeKeyIndices = [0,1,2,3];
  const rowDelimiter = '\n';
  const cellDelimiter = '\t';

  let columns = [];
  let data = [];
  if (original && translation) {
    const dataTable = parseDataTables({
      original,
      translation,
      compositeKeyIndices,
      rowDelimiter,
      cellDelimiter,
    });
    const customBodyRender = (value, tableMeta, updateValue) => (
      <Cell value={value} rowHeader={rowHeader} tableMeta={tableMeta} preview={preview} />
    );
    columns = dataTable.columnNames.map((name, index) => ({
      name,
      searchable: true,
      options: {
        display: !hideColumns.includes(index),
        filter: !noFilter.includes(index),
        customBodyRender,
      },
    }));
    if (rowHeader) {
      const headerColumn = {
        name: 'rowHeader',
        options: {
          filter: false,
          customBodyRender,
        },
      };
      columns.unshift(headerColumn);
      data = dataTable.data.map(row => ['rowHeader', ...row]);
    } else {
      data = dataTable.data;
    }
  }

  return (
    <MuiThemeProvider theme={getMuiTheme}>
      <MUIDataTable
        columns={columns}
        data={data}
        options={_options}
        {...props}
      />
    </MuiThemeProvider>
  )
}

DataTable.propTypes = {
  /* @ignore */
  classes: PropTypes.object.isRequired,
  original: PropTypes.string.isRequired,
  translation: PropTypes.string.isRequired,
  options: PropTypes.object,
  rowHeader: PropTypes.func,
  hideColumns: PropTypes.shape([PropTypes.number]),
  noFilter: PropTypes.shape([PropTypes.number]),
  compositeKeyIndices: PropTypes.shape([PropTypes.number]),
  rowDelimiter: PropTypes.string.isRequired,
  cellDelimiter: PropTypes.string.isRequired,
};

export default DataTable;
