import React, {useState} from 'react';
import MUIDataTable from "mui-datatables";
import { MuiThemeProvider } from '@material-ui/core/styles';

import { getMuiTheme } from './muiTheme';
import Cell from './Cell';
import CustomToolbar from './CustomToolbar';

import { parseDataTables } from './helpers';

function DataTable ({
  original,
  translation,
  options,
  ...props
}) {
  const [rowsPerPage, setRowsPerPage] = useState(25);
  const [preview, setPreview] = useState(true);

  const togglePreview = () => setPreview(!preview);
  const onSave = () => {};

  const _options = {
    responsive: 'scroll',
    fixedHeader: false,
    resizableColumns: false,
    selectableRows: 'none',
    rowsPerPage: rowsPerPage,
    rowsPerPageOptions: [25, 50, 100],
    onChangeRowsPerPage: setRowsPerPage,
    download: false,
    print: false,
    customToolbar: () => (
      <CustomToolbar
        preview={preview}
        onPreview={togglePreview}
        onSave={onSave}
      />
    ),
    ...options
  };

  const combineColumns = [0,1,2];
  const hideColumns = [3,6,7];
  const noFilter = [0,3,5,6,7,8];
  const compositeKeyIndices = [0,1,2,3];
  const rowDelimiter = '\n';
  const cellDelimiter = '\t';

  let columns = [];
  let data = [];
  if (original && translation) {
    const dataTable = parseDataTables({
      original,
      translation,
      compositeKeyIndices,
      rowDelimiter,
      cellDelimiter,
    });
    const _columns = dataTable.columnNames.map((name, index) => ({
      name,
      options: {
        display: !hideColumns.includes(index),
        filter: !noFilter.includes(index),
        customBodyRender: (value, tableMeta, updateValue) => (
          <Cell value={value} tableMeta={tableMeta} preview={preview} />
        ),
      },
    }));
    const headerColumn = {
      name: 'header',
      options: {
        filter: false,
        customBodyRender: (value, tableMeta) => {
          debugger
          return (
            <div>
              {`${tableMeta.rowData[0]} ${tableMeta.rowData[1]}:${tableMeta.rowData[2]}`}
            </div>
          )
        },
      },
    };
    columns.push(headerColumn);
    columns = columns.concat(_columns);
    data = dataTable.data;
    debugger
  }

  return (
    <MuiThemeProvider theme={getMuiTheme}>
      <MUIDataTable
        columns={columns}
        data={data}
        options={_options}
        {...props}
      />
    </MuiThemeProvider>
  )
}

export default DataTable;
