import React, {useState} from 'react';
import MUIDataTable from "mui-datatables";
import { createMuiTheme, MuiThemeProvider } from '@material-ui/core/styles';

function DataTable ({
  original,
  translation,
  options,
  ...props
}) {
  const [rowsPerPage, setRowsPerPage] = useState(25);
  const [preview, setPreview] = useState(true);

  const _options = {
    responsive: 'scroll',
    fixedHeader: false,
    resizableColumns: false,
    selectableRows: 'none',
    rowsPerPage: rowsPerPage,
    rowsPerPageOptions: [25, 50, 100],
    onChangeRowsPerPage: setRowsPerPage,
    download: false,
    print: false,
    ...options
  };

  let columns, data;
  if (original) {
    const originalParsed = original.split('\n').map(row => row.split('\t'));
    columns = originalParsed[0].map(name => ({
      name,
      options: {
        customBodyRender: (value, tableMeta, updateValue) => (
          <Cell value={value} tableMeta={tableMeta} preview={preview} />
        )
      }
    }));
    data = originalParsed.slice(1);
  }

  return (
    <MuiThemeProvider theme={getMuiTheme}>
      <MUIDataTable
        columns={columns}
        data={data}
        options={_options}
        {...props}
      />
    </MuiThemeProvider>
  )
}

const getMuiTheme = createMuiTheme({
  typography: {
    useNextVariants: true,
  },
  overrides: {
    MuiTableCell: {
      root: {
        padding: '8px',
        textAlign: 'unset',
        display: 'block',
        borderBottom: 'none',
      },
      body: {
        fontSize: '1em',
      },
    },
    MuiTableRow: {
      root: {
        height: 'unset',
        padding: '16px 0',
        display: 'block',
        borderBottom: '1px solid rgba(224, 224, 224, 1)',
      }
    },
    MUIDataTable: {
      root: {
      },
      responsiveScroll: {
        maxHeight: 'unset',
        overflowX: 'unset',
        overflowY: 'unset',
      },
    },
    MuiToolbar: {
      root: {
        top: 0,
        position: 'sticky',
        background: 'white',
        zIndex: '100',
      },
    },
    MUIDataTableHeadRow: {
      root: {
        display: 'none',
      },
    },
    MuiTable: {
      root: {
        position: 'sticky',
        bottom: 0,
        background: 'white',
      },
    },
  }
});


export default DataTable;
