import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core';

import { BlockEditable } from 'markdown-translatable';

const inputFilters = [[/<br>/gi, '\n']];
const outputFilters = [[/\n/gi, '<br>']];

const Cell = ({
  classes,
  value,
  tableMeta,
  tableMeta: {
    columnIndex,
    rowIndex,
    rowData,
  },
  rowHeader,
  preview,
}) => {
  const [original, translation] = value.split('\t');
  let component;

  const onEdit = (markdown) => {
    debugger
    // editCell({rowIndex, columnIndex, value: markdown});
  };

  if (value === 'rowHeader' && rowHeader) {
    component = rowHeader(rowData);
  } else {
    const subheading = (
      <Typography
        className={classes.subheading}
        variant='subtitle2'
        align='center'
        color='textSecondary'
      >
        {tableMeta.columnData.name}
      </Typography>
    )
    const originalComponent = (
      <BlockEditable
        raw={!preview}
        markdown={original || '*empty*'}
        editable={false}
        inputFilters={inputFilters}
        outputFilters={outputFilters}
      />
    );
    const translationComponent = (
      <BlockEditable
        raw={!preview}
        markdown={translation}
        editable={true}
        inputFilters={inputFilters}
        outputFilters={outputFilters}
        onEdit={onEdit}
      />
    );
    component = (
      <>
        <div className={classes.original} style={blockStyle}>
          {subheading}
          {originalComponent}
        </div>
        <div className={classes.translation} style={blockStyle}>
          {translationComponent}
        </div>
      </>
    );
  }

  return component;
};

Cell.propTypes = {
  /* @ignore */
  classes: PropTypes.object.isRequired,
  value: PropTypes.string.isRequired,
  tableMeta: PropTypes.object.isRequired,
  rowHeader: PropTypes.func.isRequired,
  preview: PropTypes.bool,
};

const blockStyle = {};

const styles = theme => ({
  root: {
  },
  original: {
    background: '#eee4',
    display: 'inline-block',
    width: '50%'
  },
  translation: {
    display: 'inline-block',
    width: '50%'
  },
  subheading: {
    margin: '4px 0 -8px 0',
  }
});

const StyleComponent = withStyles(styles)(Cell);
export default StyleComponent;
