import React from 'react';
import PropTypes from 'prop-types';

import { BlockEditable } from 'markdown-translatable';

const Cell = ({
  classes,
  value,
  tableMeta,
  tableMeta: {
    columnIndex,
    rowIndex,
  },
  preview,
}) => {
  let style = { padding: '0px', };

  const originalComponent = (
    <BlockEditable
      style={style}
      raw={!preview}
      markdown={value.original}
      editable={false}
      inputFilters={[[/<br>/gi, '\n']]}
      outputFilters={[[/\n/gi, '<br>']]}
      onEdit={(markdown) => {
        debugger
        // editCell({rowIndex, columnIndex, value: markdown});
      }}
    />
  );
  const translationComponent = (
    <BlockEditable
      style={style}
      raw={!preview}
      markdown={value.translation}
      editable={true}
      inputFilters={[[/<br>/gi, '\n']]}
      outputFilters={[[/\n/gi, '<br>']]}
      onEdit={(markdown) => {
        debugger
        // editCell({rowIndex, columnIndex, value: markdown});
      }}
    />
  );

  return (
    <>
      <div style={{display: 'inline-block', width: '50%'}}>
        {originalComponent}
      </div>
      <div style={{display: 'inline-block', width: '50%'}}>
        {translationComponent}
      </div>
    </>
  );
};

Cell.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default Cell;
